import React, { useState } from "react";
import { Grid, Stack, Typography, Button } from "@mui/material";
import HeaderLogedin from "../components/headerLogedin";
import { getProducts } from "../api/products";

export default function ProductDetail() {
  const imageColumn = {};
  const titleColumn = {
    display: "flex",
    flexDirection: "column",
    paddingInline: "30px",
    justfyContent: "flex-start",
    alignItems: "flex-satrt",
  };
  const sellerColumn = {};

  const [product, setProduct] = useState(getProducts()[0]);
  return (
    <>
      <HeaderLogedin />
      <Stack dir="rtl">
        <Typography sx={{ textAlign: "left", paddingTop: "60px" }}>
          خانه {`>`} تیرآهن {`>`} {product.title}
        </Typography>
        <Grid container sx={{ marginBlock: "30px" }}>
          <Grid sx={imageColumn} item xs={4}>
            <img
              src={require("../assets/images/girder.jpeg")}
              style={{ width: "100%", paddingBottom: "5px" }}
            />
          </Grid>
          <Grid sx={titleColumn} item xs={4}>
            <Typography variant="h2" sx={{ textAlign: "left" }}>
              {product.title}
            </Typography>
            <Stack>
              <Typography variant="h4" sx={{ textAlign: "left", mt: "50px" }}>
                از ۶,۰۰۰,۰۰۰ تومان تا ۸,۰۰۰,۰۰۰ تومان
              </Typography>

              <Button
                type="submit"
                variant="contained"
                color="primary"
                size="large"
              >
                خرید با کمترین قیمت
              </Button>
            </Stack>
          </Grid>
          <Grid sx={sellerColumn} item xs={4}></Grid>
        </Grid>
      </Stack>
    </>
  );
}
