import React from "react";
import { TextField, Button, Paper, Typography, Stack } from "@mui/material";

export default function ProfileInfo() {
  const [alignment, setAlignment] = React.useState("web");

  const inputStyle = {
    "& .MuiTextField-root": {
      paddingBottom: "10px",
      fontSize: "10px",
    },
    "& .MuiButton-root": { fontSize: "12px", px: 4, py: 1.5 },
  };
  const handleChange = (event, newAlignment) => {
    setAlignment(newAlignment);
  };
  return (
    <>
      <Stack dir="rtl" direction="row" spacing={5} sx={inputStyle}>
        <form>
          <TextField fullWidth label="نام و نام‌خانوادگی" variant="outlined" />
          <TextField fullWidth label="تلفن ثابت" variant="outlined" />
          <TextField fullWidth label="موبایل" variant="outlined" />
          <ToggleButtonGroup
            color="primary"
            value={alignment}
            exclusive
            onChange={handleChange}
            aria-label="Platform"
          >
            <ToggleButton value="web">Web</ToggleButton>
            <ToggleButton value="android">Android</ToggleButton>
            <ToggleButton value="ios">iOS</ToggleButton>
          </ToggleButtonGroup>
        </form>
        <form>
          <TextField fullWidth label="نام فروشگاه " variant="outlined" />
          <TextField fullWidth label="آدرس سایت فروشگاه " variant="outlined" />
          <TextField fullWidth label="موبایل" variant="outlined" />
        </form>
      </Stack>
    </>
  );
}
