import React, { useState } from "react";
import { styled } from "@mui/material/styles";
import { getProducts } from "../api/products";
import PN from "persian-number";
import {
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Box,
  Stack,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
} from "@mui/material";

export default function ListProducts() {
  const StyledTableCell = styled(TableCell)(({ theme }) => ({
    backgroundColor: theme.palette.custom.main,
    color: theme.palette.primary.main,
    fontSize: "9px",
    fontWeight: 600,
    padding: "0px",
    textAlign: "center",
    borderStyle: "solid",
    borderWidth: "2px",
    borderColor: "white",
  }));
  const BodyTableCell = styled(TableCell)(({ theme }) => ({
    fontSize: "9px",
    color: theme.palette.primary.main,
    fontWeight: 600,
    textAlign: "center",
    padding: "5px",
    borderWidthBottom: "0.5px",
  }));
  const StyldedTableRow = styled(TableRow)(({ theme }) => ({
    "&:hover": {
      backgroundColor: theme.palette.custom.main,
    },
  }));

  const FormControlStyle = {
    backgroundColor: "custom.main",
    borderRadius: "5px",
    marginBottom: "7px",
  };

  const [products, setProducts] = useState(getProducts());

  const [location, setLocation] = useState("");
  const [size, setSize] = useState("");
  const [type, setType] = useState("");

  const handleChange = (event) => {
    setLocation(event.target.value);
  };

  return (
    <Stack dir="rtl" direction="row">
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <StyledTableCell style={{ width: "5%" }}>ردیف</StyledTableCell>
              <StyledTableCell>نام</StyledTableCell>
              <StyledTableCell>آنالیز</StyledTableCell>
              <StyledTableCell>محل بارگیری</StyledTableCell>
              <StyledTableCell>حالت</StyledTableCell>
              <StyledTableCell>سایز</StyledTableCell>
              <StyledTableCell>نوع</StyledTableCell>
              <StyledTableCell>واحد</StyledTableCell>
              <StyledTableCell>قیمت</StyledTableCell>
              <StyledTableCell>۹٪ ارزش افزوده</StyledTableCell>
              <StyledTableCell>نمودار</StyledTableCell>
              <StyledTableCell>خرید</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {products.map((product, index) => (
              <StyldedTableRow key={index}>
                <BodyTableCell>{PN.convertEnToPe(index + 1)}</BodyTableCell>
                <BodyTableCell
                  sx={{
                    "&:hover": {
                      color: "secondary.main",
                    },
                  }}
                >
                  {product.title}
                </BodyTableCell>
                <BodyTableCell>{product.analysis}</BodyTableCell>
                <BodyTableCell>{product.loading}</BodyTableCell>
                <BodyTableCell>{product.state}</BodyTableCell>
                <BodyTableCell>{PN.convertEnToPe(product.size)}</BodyTableCell>
                <BodyTableCell>{product.type}</BodyTableCell>
                <BodyTableCell>{product.unit}</BodyTableCell>
                <BodyTableCell>{PN.convertEnToPe(product.price)}</BodyTableCell>
                <BodyTableCell>{PN.convertEnToPe(product.tax)}</BodyTableCell>
                <BodyTableCell>نمودار</BodyTableCell>
                <BodyTableCell>خرید</BodyTableCell>
              </StyldedTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <div style={{ width: "25%" }}>
        <Box
          sx={{
            ml: "10px",
            backgroundColor: "custom.main",
            borderRadius: "5px",
            padding: "5px",
          }}
        >
          <Box
            sx={{
              backgroundColor: "#fff",
              paddingTop: "5px",
              paddingX: "5px",
              borderRadius: "5px",
            }}
          >
            <FormControl fullWidth sx={FormControlStyle}>
              <InputLabel sx={{ color: "text.main" }} id="select-label">
                محل بارگیری
              </InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={location}
                label="محل بارگیری"
                onChange={(event) => setLocation(event.target.value)}
              >
                <MenuItem dir="rtl" value={10}>
                  کارخانه
                </MenuItem>
                <MenuItem dir="rtl" value={20}>
                  کارخانه 2
                </MenuItem>
              </Select>
            </FormControl>

            <FormControl fullWidth sx={FormControlStyle}>
              <InputLabel sx={{ color: "text.main" }} id="select-label2">
                سایز
              </InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={size}
                label="سایز"
                onChange={(event) => setSize(event.target.value)}
              >
                <MenuItem dir="rtl" value={30}>
                  کارخانه
                </MenuItem>
                <MenuItem dir="rtl" value={40}>
                  کارخانه 2
                </MenuItem>
              </Select>
            </FormControl>
          </Box>
        </Box>
      </div>
    </Stack>
  );
}
