import { Box, Button, Stack, Toolbar, Typography } from "@mui/material";
import React from "react";
import { Link, NavLink } from "react-router-dom";

const sxStyle = {
  fontWeight: 600,
  fontSize: {
    xs: "9px",
    sm: "12px",
    md: "12px",
  },
  padding: "40px",
  fontDecoration: "none",
  "&:hover": {
    bgcolor: "white",
    color: "secondary.main",
  },
  p: 0,
};

export default function Header() {
  //const theme = useTheme();
  //const isMobile = useMediaQuery(theme.breakPoints.down("xs"))
  return (
    <Stack direction="row" sx={{ pt: "20px" }}>
      <Stack direction="row" spacing={0} sx={{ flex: 1 }}>
        <Button sx={sxStyle} variant="outlined" component={Link} to="/login">
          عضویت
        </Button>
        <Button sx={sxStyle} component={Link} to="/register">
          ورود
        </Button>
      </Stack>

      <Stack
        direction="row"
        dir="rtl"
        spacing={{ xs: "0px", sm: "12px", md: "10px", lg: "18px" }}
        sx={{ flex: 2, justifyContent: "center" }}
      >
        <Typography variant="h6" component={NavLink} to="/">
          خانه
        </Typography>
        <Typography
          sx={sxStyle}
          variant="text"
          component={NavLink}
          to="/products"
        >
          محصولات
        </Typography>
        <Button sx={sxStyle} variant="text" component={NavLink} to="/price">
          قیمت آنلاین
        </Button>
        <Button sx={sxStyle} variant="text" component={NavLink} to="/about">
          درباره ما
        </Button>
        <Button sx={sxStyle} variant="text" component={NavLink} to="/contact">
          تماس با ما
        </Button>
      </Stack>
      <Stack direction="row" sx={{ flex: 1, justifyContent: "flex-end" }}>
        <img
          style={{ width: "100%", maxWidth: "100px" }}
          src={require("../assets/images/Logo601.png")}
        />
      </Stack>
    </Stack>
  );
}
